import { NextRequest, NextResponse } from 'next/server'
import { auth } from '@clerk/nextjs/server'
import { pool } from '@/lib/db'

export async function POST(
  request: NextRequest,
  { params }: { params: Promise<{ commentId: string }> }
) {
  try {
    const { userId } = await auth()
    if (!userId) {
      return NextResponse.json({ error: 'Authentication required' }, { status: 401 })
    }

    const { commentId } = await params

    // Check if user already liked this comment
    const existingLike = await pool.query(
      'SELECT id FROM comment_likes WHERE comment_id = $1 AND clerk_user_id = $2',
      [commentId, userId]
    )

    if (existingLike.rows.length > 0) {
      // Unlike: remove the like
      await pool.query(
        'DELETE FROM comment_likes WHERE comment_id = $1 AND clerk_user_id = $2',
        [commentId, userId]
      )
      return NextResponse.json({ liked: false })
    } else {
      // Like: add the like
      await pool.query(
        'INSERT INTO comment_likes (comment_id, clerk_user_id) VALUES ($1, $2)',
        [commentId, userId]
      )
      return NextResponse.json({ liked: true })
    }
  } catch (error) {
    console.error('Error toggling like:', error)
    return NextResponse.json({ error: 'Failed to toggle like' }, { status: 500 })
  }
}

'use client'

import { useState, useEffect } from 'react'
import { useUser } from '@clerk/nextjs'
import { CommentsList } from './CommentsList'
import { CommentForm } from './CommentForm'
import { SignInPrompt } from './SignInPrompt'
import { Comment } from '@/lib/db'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Separator } from '@/components/ui/separator'

interface CommentSectionProps {
  postSlug: string
}

export function CommentSection({ postSlug }: CommentSectionProps) {
  const [comments, setComments] = useState<Comment[]>([])
  const [loading, setLoading] = useState(true)
  const { isSignedIn } = useUser()

  const fetchComments = async () => {
    try {
      const response = await fetch(`/api/comments?post_slug=${postSlug}`)
      if (response.ok) {
        const data = await response.json()
        setComments(data)
      }
    } catch (error) {
      console.error('Error fetching comments:', error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchComments()
  }, [postSlug])

  const handleCommentAdded = (newComment: Comment) => {
    if (newComment.parent_id) {
      // It's a reply - add to the parent's replies
      setComments(prev => prev.map(comment => {
        if (comment.id === newComment.parent_id) {
          return {
            ...comment,
            replies: [...(comment.replies || []), newComment]
          }
        }
        return comment
      }))
    } else {
      // It's a top-level comment
      setComments(prev => [...prev, { ...newComment, replies: [] }])
    }
  }

  const handleLikeToggle = (commentId: number, liked: boolean) => {
    setComments(prev => prev.map(comment => {
      if (comment.id === commentId) {
        return {
          ...comment,
          user_has_liked: liked,
          like_count: (comment.like_count || 0) + (liked ? 1 : -1)
        }
      }
      // Also check replies
      if (comment.replies) {
        comment.replies = comment.replies.map(reply => {
          if (reply.id === commentId) {
            return {
              ...reply,
              user_has_liked: liked,
              like_count: (reply.like_count || 0) + (liked ? 1 : -1)
            }
          }
          return reply
        })
      }
      return comment
    }))
  }

  if (loading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="text-center text-muted-foreground">Loading comments...</div>
        </CardContent>
      </Card>
    )
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="text-xl font-mono">Comments ({comments.length})</CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {isSignedIn ? (
          <CommentForm 
            postSlug={postSlug} 
            onCommentAdded={handleCommentAdded}
          />
        ) : (
          <SignInPrompt />
        )}

        <Separator />

        <CommentsList 
          comments={comments}
          postSlug={postSlug}
          onCommentAdded={handleCommentAdded}
          onLikeToggle={handleLikeToggle}
        />
      </CardContent>
    </Card>
  )
}

