import { NextRequest, NextResponse } from 'next/server'
import { auth, currentUser } from '@clerk/nextjs/server'
import { pool } from '@/lib/db'

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const postSlug = searchParams.get('post_slug')

    if (!postSlug) {
      return NextResponse.json({ error: 'Post slug is required' }, { status: 400 })
    }

    const { userId } = await auth()

    // Get comments with like counts and user's like status
    const query = `
      SELECT 
        c.*,
        COUNT(cl.id) as like_count,
        ${userId ? `BOOL_OR(cl.clerk_user_id = $2) as user_has_liked` : 'false as user_has_liked'}
      FROM comments c
      LEFT JOIN comment_likes cl ON c.id = cl.comment_id
      WHERE c.post_slug = $1
      GROUP BY c.id
      ORDER BY c.created_at ASC
    `

    const values = userId ? [postSlug, userId] : [postSlug]
    const result = await pool.query(query, values)

    // Organize comments in a tree structure
    const comments = result.rows
    const commentTree = comments.filter(c => !c.parent_id)

    // Add replies to each comment
    commentTree.forEach(comment => {
      comment.replies = comments.filter(c => c.parent_id === comment.id)
    })

    return NextResponse.json(commentTree)
  } catch (error) {
    console.error('Error fetching comments:', error)
    return NextResponse.json({ error: 'Failed to fetch comments' }, { status: 500 })
  }
}

export async function POST(request: NextRequest) {
  try {
    const { userId } = await auth()
    if (!userId) {
      return NextResponse.json({ error: 'Authentication required' }, { status: 401 })
    }

    const user = await currentUser()
    if (!user) {
      return NextResponse.json({ error: 'User not found' }, { status: 401 })
    }

    const { post_slug, content, parent_id } = await request.json()

    if (!post_slug || !content) {
      return NextResponse.json({ error: 'Post slug and content are required' }, { status: 400 })
    }

    const username = user.firstName || user.username || 'Anonymous'

    const query = `
      INSERT INTO comments (post_slug, clerk_user_id, username, content, parent_id)
      VALUES ($1, $2, $3, $4, $5)
      RETURNING *
    `

    const values = [post_slug, userId, username, content, parent_id || null]
    const result = await pool.query(query, values)

    return NextResponse.json(result.rows[0], { status: 201 })
  } catch (error) {
    console.error('Error creating comment:', error)
    return NextResponse.json({ error: 'Failed to create comment' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { auth } from '@clerk/nextjs/server'
import { pool } from '@/lib/db'

export async function POST(
  request: NextRequest,
  { params }: { params: Promise<{ commentId: string }> }
) {
  try {
    const { userId } = await auth()
    if (!userId) {
      return NextResponse.json({ error: 'Authentication required' }, { status: 401 })
    }

    const { commentId } = await params

    // Check if user already liked this comment
    const existingLike = await pool.query(
      'SELECT id FROM comment_likes WHERE comment_id = $1 AND clerk_user_id = $2',
      [commentId, userId]
    )

    if (existingLike.rows.length > 0) {
      // Unlike: remove the like
      await pool.query(
        'DELETE FROM comment_likes WHERE comment_id = $1 AND clerk_user_id = $2',
        [commentId, userId]
      )
      return NextResponse.json({ liked: false })
    } else {
      // Like: add the like
      await pool.query(
        'INSERT INTO comment_likes (comment_id, clerk_user_id) VALUES ($1, $2)',
        [commentId, userId]
      )
      return NextResponse.json({ liked: true })
    }
  } catch (error) {
    console.error('Error toggling like:', error)
    return NextResponse.json({ error: 'Failed to toggle like' }, { status: 500 })
  }
}

