'use client'

import { useState, useEffect } from 'react'
import { useUser } from '@clerk/nextjs'
import { CommentsList } from './CommentsList'
import { CommentForm } from './CommentForm'
import { SignInPrompt } from './SignInPrompt'
import { Comment } from '@/lib/db'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Separator } from '@/components/ui/separator'

interface CommentSectionProps {
  postSlug: string
}

export function CommentSection({ postSlug }: CommentSectionProps) {
  const [comments, setComments] = useState<Comment[]>([])
  const [loading, setLoading] = useState(true)
  const { isSignedIn } = useUser()

  const fetchComments = async () => {
    try {
      const response = await fetch(`/api/comments?post_slug=${postSlug}`)
      if (response.ok) {
        const data = await response.json()
        setComments(data)
      }
    } catch (error) {
      console.error('Error fetching comments:', error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchComments()
  }, [postSlug])

  const handleCommentAdded = (newComment: Comment) => {
    if (newComment.parent_id) {
      // It's a reply - add to the parent's replies
      setComments(prev => prev.map(comment => {
        if (comment.id === newComment.parent_id) {
          return {
            ...comment,
            replies: [...(comment.replies || []), newComment]
          }
        }
        return comment
      }))
    } else {
      // It's a top-level comment
      setComments(prev => [...prev, { ...newComment, replies: [] }])
    }
  }

  const handleLikeToggle = (commentId: number, liked: boolean) => {
    setComments(prev => prev.map(comment => {
      if (comment.id === commentId) {
        return {
          ...comment,
          user_has_liked: liked,
          like_count: (comment.like_count || 0) + (liked ? 1 : -1)
        }
      }
      // Also check replies
      if (comment.replies) {
        comment.replies = comment.replies.map(reply => {
          if (reply.id === commentId) {
            return {
              ...reply,
              user_has_liked: liked,
              like_count: (reply.like_count || 0) + (liked ? 1 : -1)
            }
          }
          return reply
        })
      }
      return comment
    }))
  }

  if (loading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="text-center text-muted-foreground">Loading comments...</div>
        </CardContent>
      </Card>
    )
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="text-xl font-mono">Comments ({comments.length})</CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {isSignedIn ? (
          <CommentForm 
            postSlug={postSlug} 
            onCommentAdded={handleCommentAdded}
          />
        ) : (
          <SignInPrompt />
        )}

        <Separator />

        <CommentsList 
          comments={comments}
          postSlug={postSlug}
          onCommentAdded={handleCommentAdded}
          onLikeToggle={handleLikeToggle}
        />
      </CardContent>
    </Card>
  )
}

'use client'

import { useState } from 'react'
import { useUser } from '@clerk/nextjs'
import { formatDistanceToNow } from 'date-fns'
import { CommentForm } from './CommentForm'
import { Comment } from '@/lib/db'
import { Button } from '@/components/ui/button'
import { Avatar, AvatarFallback } from '@/components/ui/avatar'
import { Heart, MessageCircle } from 'lucide-react'

interface CommentsListProps {
  comments: Comment[]
  postSlug: string
  onCommentAdded: (comment: Comment) => void
  onLikeToggle: (commentId: number, liked: boolean) => void
}

export function CommentsList({ comments, postSlug, onCommentAdded, onLikeToggle }: CommentsListProps) {
  const [replyingTo, setReplyingTo] = useState<number | null>(null)
  const { isSignedIn } = useUser()

  const handleLike = async (commentId: number) => {
    if (!isSignedIn) return

    try {
      const response = await fetch(`/api/comments/${commentId}/like`, {
        method: 'POST',
      })

      if (response.ok) {
        const data = await response.json()
        onLikeToggle(commentId, data.liked)
      }
    } catch (error) {
      console.error('Error toggling like:', error)
    }
  }

  const CommentItem = ({ comment, isReply = false }: { comment: Comment; isReply?: boolean }) => (
    <div className={`space-y-3 ${isReply ? 'ml-8 pl-4 border-l-2 border-muted' : ''}`}>
      <div className="flex items-start space-x-3">
        <Avatar className="h-8 w-8">
          <AvatarFallback className="text-xs">
            {comment.username.charAt(0).toUpperCase()}
          </AvatarFallback>
        </Avatar>

        <div className="flex-1 space-y-2">
          <div className="flex items-center space-x-2">
            <span className="font-semibold text-sm">{comment.username}</span>
            <span className="text-xs text-muted-foreground">
              {formatDistanceToNow(new Date(comment.created_at), { addSuffix: true })}
            </span>
          </div>

          <p className="text-sm leading-relaxed">{comment.content}</p>

          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              size="sm"
              className="h-8 px-2 hover:bg-accent"
              onClick={() => handleLike(comment.id)}
              disabled={!isSignedIn}
            >
              <Heart 
                className={`h-4 w-4 mr-1 ${
                  comment.user_has_liked ? 'fill-red-500 text-red-500' : ''
                }`} 
              />
              <span className="text-xs">{comment.like_count || 0}</span>
            </Button>

            {!isReply && isSignedIn && (
              <Button
                variant="ghost"
                size="sm"
                className="h-8 px-2 hover:bg-accent"
                onClick={() => setReplyingTo(replyingTo === comment.id ? null : comment.id)}
              >
                <MessageCircle className="h-4 w-4 mr-1" />
                <span className="text-xs">Reply</span>
              </Button>
            )}
          </div>

          {replyingTo === comment.id && (
            <div className="mt-3">
              <CommentForm
                postSlug={postSlug}
                parentId={comment.id}
                onCommentAdded={(newComment) => {
                  onCommentAdded(newComment)
                  setReplyingTo(null)
                }}
                placeholder={`Reply to ${comment.username}...`}
                compact
              />
            </div>
          )}
        </div>
      </div>

      {comment.replies && comment.replies.length > 0 && (
        <div className="mt-4 space-y-4">
          {comment.replies.map((reply) => (
            <CommentItem key={reply.id} comment={reply} isReply={true} />
          ))}
        </div>
      )}
    </div>
  )

  if (comments.length === 0) {
    return (
      <div className="text-center py-8 text-muted-foreground">
        <MessageCircle className="h-12 w-12 mx-auto mb-3 opacity-50" />
        <p>No comments yet. Be the first to share your thoughts!</p>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {comments.map((comment) => (
        <CommentItem key={comment.id} comment={comment} />
      ))}
    </div>
  )
}

'use client'

import { useState } from 'react'
import { useUser } from '@clerk/nextjs'
import { Comment } from '@/lib/db'
import { Button } from '@/components/ui/button'
import { Textarea } from '@/components/ui/textarea'
import { Avatar, AvatarFallback } from '@/components/ui/avatar'
import { Send } from 'lucide-react'

interface CommentFormProps {
  postSlug: string
  parentId?: number
  onCommentAdded: (comment: Comment) => void
  placeholder?: string
  compact?: boolean
}

export function CommentForm({ 
  postSlug, 
  parentId, 
  onCommentAdded, 
  placeholder = "Share your thoughts...",
  compact = false 
}: CommentFormProps) {
  const [content, setContent] = useState('')
  const [isSubmitting, setIsSubmitting] = useState(false)
  const { user } = useUser()

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!content.trim() || isSubmitting) return

    setIsSubmitting(true)

    try {
      const response = await fetch('/api/comments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          post_slug: postSlug,
          content: content.trim(),
          parent_id: parentId
        })
      })

      if (response.ok) {
        const newComment = await response.json()
        onCommentAdded(newComment)
        setContent('')
      }
    } catch (error) {
      console.error('Error posting comment:', error)
    } finally {
      setIsSubmitting(false)
    }
  }

  const username = user?.firstName || user?.username || 'Anonymous'

  return (
    <form onSubmit={handleSubmit} className="space-y-3">
      <div className="flex items-start space-x-3">
        {!compact && (
          <Avatar className="h-8 w-8">
            <AvatarFallback className="text-xs">
              {username.charAt(0).toUpperCase()}
            </AvatarFallback>
          </Avatar>
        )}

        <div className="flex-1 space-y-3">
          <Textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder={placeholder}
            className={`resize-none ${compact ? 'min-h-[80px]' : 'min-h-[100px]'}`}
            disabled={isSubmitting}
          />

          <div className="flex justify-end">
            <Button 
              type="submit" 
              disabled={!content.trim() || isSubmitting}
              size={compact ? "sm" : "default"}
            >
              <Send className="h-4 w-4 mr-2" />
              {isSubmitting ? 'Posting...' : (parentId ? 'Reply' : 'Comment')}
            </Button>
          </div>
        </div>
      </div>
    </form>
  )
}

